CREATE OR REPLACE PROCEDURE pls_converte_codigo_mat_tuss ( nr_seq_material_p bigint, cd_material_tuss_p INOUT bigint, nr_seq_mat_tuss_p INOUT bigint) AS $body$
DECLARE


qt_registro_w			bigint;
cd_material_tuss_w		tuss_material_item.cd_material_tuss%type;
nr_seq_mat_tuss_w		tuss_material_item.nr_sequencia%type;


BEGIN

select	count(1)
into STRICT	qt_registro_w
from	pls_material_tuss
where	nr_seq_material =  nr_seq_material_p;


if ( qt_registro_w > 0 ) then
	begin
		select	nr_seq_tuss_mat_item,
			obter_dados_mat_tuss(nr_seq_tuss_mat_item,'C')
		into STRICT	nr_seq_mat_tuss_w,
			cd_material_tuss_w
		from	pls_material_tuss
		where	nr_seq_material = nr_seq_material_p
		and 	dt_vigencia_inicial <= clock_timestamp()
		and (coalesce(dt_vigencia_final::text, '') = '' or dt_vigencia_final > clock_timestamp());
	exception
	when others then
		nr_seq_mat_tuss_w 	:= null;
		cd_material_tuss_w	:= null;
	end;
end if;

nr_seq_mat_tuss_p	:= nr_seq_mat_tuss_w;
cd_material_tuss_p	:= cd_material_tuss_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
