CREATE OR REPLACE PROCEDURE pls_convert_mat_guia_xml_mens ( nr_seq_material_p bigint, nr_seq_segurado_p bigint, ie_tipo_guia_p pls_conta.ie_tipo_guia%type, nr_seq_mat_convertido_p INOUT bigint, nr_seq_regra_p INOUT bigint) AS $body$
DECLARE


nr_seq_material_conversao_w	bigint;
nr_seq_conver_mat_guia_w	bigint;
ie_tipo_despesa_w		varchar(2);
nr_seq_contrato_w		bigint;
nr_seq_intercambio_w		bigint;
nr_seq_plano_w			bigint;

c01 CURSOR FOR
	SELECT	nr_seq_mat_conversao,
		nr_sequencia
	from	pls_conversao_mat_guias
	where	((pls_obter_se_mat_estrutura(nr_seq_material_p,nr_seq_estrutura_mat) = 'S') or (coalesce(nr_seq_estrutura_mat::text, '') = ''))
	and	((nr_seq_material	= nr_seq_material_p) or (coalesce(nr_seq_material::text, '') = ''))
	and	((ie_tipo_despesa	= ie_tipo_despesa_w) or (coalesce(ie_tipo_despesa::text, '') = ''))
	and	((nr_seq_contrato	= nr_seq_contrato_w) or (coalesce(nr_seq_contrato::text, '') = ''))
	and	((nr_seq_intercambio	= nr_seq_intercambio_w) or (coalesce(nr_seq_intercambio::text, '') = ''))
	and	((nr_seq_plano		= nr_seq_plano_w) or (coalesce(nr_seq_plano::text, '') = ''))
	and	((ie_tipo_guia		= ie_tipo_guia_p) or (coalesce(ie_tipo_guia::text, '') = ''))
	and	ie_situacao		= 'A'
	order by coalesce(nr_seq_material,0),
		coalesce(nr_seq_estrutura_mat,0),
		coalesce(ie_tipo_despesa,' '),
		coalesce(nr_seq_plano,0),
		coalesce(nr_seq_contrato,0),
		coalesce(nr_seq_intercambio,0);


BEGIN
begin
select	ie_tipo_despesa
into STRICT	ie_tipo_despesa_w
from	pls_material
where	nr_sequencia	= nr_seq_material_p;
exception
when others then
	ie_tipo_despesa_w	:= null;
end;

begin
select	nr_seq_contrato,
	nr_seq_intercambio,
	nr_seq_plano
into STRICT	nr_seq_contrato_w,
	nr_seq_intercambio_w,
	nr_seq_plano_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;
exception
when others then
	nr_seq_contrato_w	:= null;
	nr_seq_intercambio_w	:= null;
	nr_seq_plano_w		:= null;
end;

open c01;
loop
fetch c01 into
	nr_seq_material_conversao_w,
	nr_seq_conver_mat_guia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

nr_seq_mat_convertido_p	:= nr_seq_material_conversao_w;
nr_seq_regra_p		:= nr_seq_conver_mat_guia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
