CREATE OR REPLACE PROCEDURE pls_copiar_cadastro_regra ( cd_estab_origem_p bigint, cd_estab_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
Procedure criada, para serem colocadas todas as procedures que irá copiar os cadastros das regas gerais do Plano de Sáude Tasy
*/
BEGIN

/*Copiar Plano de Saúde / OPS - Produtos e regras */

CALL pls_copiar_prod_regra_estab(cd_estab_origem_p,cd_estab_destino_p,nm_usuario_p);

/*Copiar Plano de Saúde / OPS - Contratos e Beneficiários */

CALL pls_copiar_plano_contra_benef(cd_estab_origem_p,cd_estab_destino_p,nm_usuario_p);

/*Copiar Plano de Saúde / OPS - Comercial */

CALL pls_copiar_regras_comercial(cd_estab_origem_p,cd_estab_destino_p,nm_usuario_p);

/*Copiar Plano de Saúde / OPS - Rede credenciada*/

CALL pls_copiar_rede_credenciada(cd_estab_origem_p,cd_estab_destino_p,nm_usuario_p);

/*Copíar Plano de Saúde / OPS - Atendimento*/

CALL pls_copiar_atend_estab(cd_estab_origem_p,cd_estab_destino_p,nm_usuario_p);

/*Copíar Plano de Saúde / OPS - Contas Médicas*/

CALL pls_copiar_contas_medicas(cd_estab_origem_p,cd_estab_destino_p,nm_usuario_p);

/*Copia Plano de Saúde / OPS - Mensalidades*/

CALL pls_copiar_regras_mensalidade(cd_estab_origem_p,cd_estab_destino_p,nm_usuario_p);

/*Copia Plano de Saúde / OPS - Informações ANS*/

CALL pls_copiar_diops_estab(cd_estab_origem_p,cd_estab_destino_p,nm_usuario_p);

end;
