SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_copiar_carencia_contrato ( nr_seq_contrato_p bigint, nr_seq_plano_p pls_plano.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE


nr_seq_plano_w			bigint;
nr_seq_carencia_w		bigint;
nr_seq_tipo_carencia_w		bigint;
qt_dias_w			bigint;
qt_carencias_w			bigint;
nr_seq_grupo_carencia_w		bigint;
qt_carencias_produto_w		bigint;
dt_contrato_w			pls_contrato.dt_contrato%type;

C01 CURSOR FOR
	SELECT	nr_seq_plano
	from	pls_contrato_plano
	where	nr_seq_contrato	= nr_seq_contrato_p
    and ((nr_seq_plano = nr_seq_plano_p) or (coalesce(nr_seq_plano_p::text, '') = ''))
	and	ie_situacao	= 'A';

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_carencia
	where	nr_seq_plano	= nr_seq_plano_w
	and	dt_contrato_w between coalesce(dt_inicio_vig_plano,dt_contrato_w) and coalesce(dt_fim_vig_plano,dt_contrato_w);


BEGIN

select	max(dt_contrato)
into STRICT	dt_contrato_w
from	pls_contrato
where	nr_sequencia	= nr_seq_contrato_p;

open C01;
loop
fetch C01 into
	nr_seq_plano_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		nr_seq_carencia_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		select	nr_seq_tipo_carencia,
			qt_dias,
			nr_seq_grupo_carencia
		into STRICT	nr_seq_tipo_carencia_w,
			qt_dias_w,
			nr_seq_grupo_carencia_w
		from	pls_carencia
		where	nr_sequencia	= nr_seq_carencia_w;
		
		if (nr_seq_tipo_carencia_w IS NOT NULL AND nr_seq_tipo_carencia_w::text <> '') then
			select	count(1)
			into STRICT	qt_carencias_w
			from	pls_carencia
			where	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_w
			and	nr_seq_contrato		= nr_seq_contrato_p
			and	((nr_seq_plano_contrato	= nr_seq_plano_w) or (coalesce(nr_seq_plano_contrato::text, '') = ''));
		elsif (nr_seq_grupo_carencia_w IS NOT NULL AND nr_seq_grupo_carencia_w::text <> '') then
			select	count(1)
			into STRICT	qt_carencias_w
			from	pls_carencia
			where	nr_seq_grupo_carencia	= nr_seq_grupo_carencia_w
			and	nr_seq_contrato		= nr_seq_contrato_p
			and	((nr_seq_plano_contrato	= nr_seq_plano_w) or (coalesce(nr_seq_plano_contrato::text, '') = ''));
		end if;	
		
		if (qt_carencias_w	= 0) then
			insert into pls_carencia(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
					nr_seq_contrato,nr_seq_tipo_carencia,qt_dias,ie_mes_posterior,ie_origem_carencia_benef,
					nr_seq_grupo_carencia,nr_seq_plano_contrato,ie_cpt)
			values (	nextval('pls_carencia_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
					nr_seq_contrato_p,nr_seq_tipo_carencia_w,qt_dias_w,'N','P',
					nr_seq_grupo_carencia_w,nr_seq_plano_w,'N');
		end if;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
