SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_copiar_carencia ( nr_seq_plano_p bigint, nr_seq_contrato_p bigint, nr_seq_segurado_p bigint, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE


ie_mes_posterior_w		varchar(1);
nr_seq_tipo_carencia_w		bigint;
nr_seq_carencia_w		bigint;
qt_registros_w			bigint;
nr_seq_grupo_carencia_w		bigint;
qt_dias_w			integer;
dt_inclusao_operadora_w		timestamp;
dt_fim_vigencia_w		timestamp;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_tipo_carencia,
		qt_dias,
		ie_mes_posterior,
		nr_seq_grupo_carencia
	from	pls_carencia
	where	nr_seq_plano	= nr_seq_plano_p
	and	coalesce(nr_seq_contrato_p,0) = 0
	and	dt_inclusao_operadora_w between coalesce(dt_inicio_vig_plano,dt_inclusao_operadora_w) and coalesce(dt_fim_vig_plano,dt_inclusao_operadora_w)
	
union

	SELECT	nr_sequencia,
		nr_seq_tipo_carencia,
		qt_dias,
		ie_mes_posterior,
		nr_seq_grupo_carencia
	from	pls_carencia
	where	nr_seq_contrato	= nr_seq_contrato_p
	and	((nr_seq_plano_contrato = nr_seq_plano_p) or (coalesce(nr_seq_plano_contrato::text, '') = ''));


BEGIN
select	max(dt_inclusao_operadora)
into STRICT	dt_inclusao_operadora_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

if (nr_seq_plano_p <> 0) or (nr_seq_contrato_p <> 0) then
	open C01;
	loop
	fetch C01 into
		nr_seq_carencia_w,
		nr_seq_tipo_carencia_w,
		qt_dias_w,
		ie_mes_posterior_w,
		nr_seq_grupo_carencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (nr_seq_tipo_carencia_w IS NOT NULL AND nr_seq_tipo_carencia_w::text <> '') then
			select	count(1)
			into STRICT	qt_registros_w
			from	pls_carencia
			where	nr_seq_segurado		= nr_seq_segurado_p
			and	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_w;
		elsif (nr_seq_grupo_carencia_w IS NOT NULL AND nr_seq_grupo_carencia_w::text <> '') then
			select	count(1)
			into STRICT	qt_registros_w
			from	pls_carencia
			where	nr_seq_segurado		= nr_seq_segurado_p
			and	nr_seq_grupo_carencia	= nr_seq_grupo_carencia_w;
		end if;

		/*aaschlote OS - 279915*/

		if (pls_consistir_sexo_carencia(nr_seq_segurado_p,nr_seq_tipo_carencia_w) = 'S') then
			if (qt_registros_w = 0) then

				dt_fim_vigencia_w	:= dt_inclusao_operadora_w + qt_dias_w;

				insert	into	pls_carencia(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
						nr_seq_plano,nr_seq_contrato,nr_seq_tipo_carencia,nr_seq_segurado,qt_dias,
						dt_inicio_vigencia,ds_observacao,ie_mes_posterior,ie_origem_carencia_benef,nr_seq_grupo_carencia,
						ie_cpt,dt_fim_vigencia)
				values (	nextval('pls_carencia_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
						null,null,nr_seq_tipo_carencia_w,nr_seq_segurado_p,qt_dias_w,
						dt_inclusao_operadora_w,'',ie_mes_posterior_w,'P',nr_seq_grupo_carencia_w,
						'N',dt_fim_vigencia_w);
			end if;
		end if;
		end;
	end loop;
	close C01;
end if;

if (coalesce(ie_commit_p,'N')	= 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
