CREATE OR REPLACE PROCEDURE pls_copiar_caren_regra_isenta ( nr_seq_regra_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_tipo_carencia_w	bigint;
qt_dias_padrao_w	bigint;
qt_registros_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		qt_dias_padrao
	from	pls_tipo_carencia
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	ie_situacao		= 'A'
	and	ie_tipo_operacao	= 'P'
	and	ie_padrao		= 'S';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_tipo_carencia_w,
	qt_dias_padrao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (coalesce(qt_dias_padrao_w::text, '') = '') then
		qt_dias_padrao_w	:= 0;
	end if;

	select	count(*)
	into STRICT	qt_registros_w
	from	pls_regra_carencia_dias
	where	nr_seq_regra		= nr_seq_regra_p
	and	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_w;

	if (qt_registros_w = 0) then
		insert into pls_regra_carencia_dias(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				nr_seq_regra,nr_seq_tipo_carencia,qt_dias)
		values (	nextval('pls_regra_carencia_dias_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				nr_seq_regra_p,nr_seq_tipo_carencia_w,qt_dias_padrao_w);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
