CREATE OR REPLACE PROCEDURE pls_copiar_cargo_diops ( cd_estab_origem_p bigint, cd_estab_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_cargo_w		bigint;
ds_cargo_w		varchar(60);
qt_registros_w		bigint;
ie_situacao_w		varchar(1);
cd_cargo_max_w		bigint;

c01 CURSOR FOR
	SELECT	cd_cargo,
		ds_cargo,
		ie_situacao
	from	pls_cargo_diops
	where	cd_estabelecimento	= cd_estab_origem_p;


BEGIN

open c01;
loop
fetch c01 into
	cd_cargo_w,
	ds_cargo_w,
	ie_situacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	select	count(*)
	into STRICT	qt_registros_w
	from	pls_cargo_diops
	where	nr_seq_anterior		= cd_cargo_w
	and	cd_estabelecimento	= cd_estab_destino_p;

	if (qt_registros_w = 0) then
		select	max(cd_cargo)+1
		into STRICT	cd_cargo_max_w
		from	pls_cargo_diops;

		insert into pls_cargo_diops(cd_cargo, nr_seq_anterior, dt_atualizacao,
			nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			ds_cargo, ie_situacao, cd_estabelecimento)
		values (cd_cargo_max_w, cd_cargo_w, clock_timestamp(),
			nm_usuario_p, clock_timestamp(), nm_usuario_p,
			ds_cargo_w, ie_situacao_w, cd_estab_destino_p);
	end if;
end loop;
close c01;


/*NÃ£o pode dar commit*/

--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
