CREATE OR REPLACE PROCEDURE pls_copiar_coberturas_produto ( nr_seq_contrato_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_plano_w			bigint;
nr_seq_cobertura_w		bigint;
nr_seq_tipo_cobertura_w		bigint;
ie_tipo_atendimento_w		varchar(2);
ie_sexo_w			varchar(2);
qt_coberturas_w			bigint;

C01 CURSOR FOR
	SELECT	nr_seq_plano
	from	pls_contrato_plano
	where	nr_seq_contrato = nr_seq_contrato_p
	and	ie_situacao = 'A';

C02 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_tipo_cobertura,
		ie_tipo_atendimento,
		ie_sexo
	from	pls_cobertura
	where	nr_seq_plano = nr_seq_plano_w
	and 	ie_situacao = 'A'
	and 	clock_timestamp() between coalesce(dt_inicio_vigencia, clock_timestamp()) and coalesce(dt_fim_vigencia, clock_timestamp());


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_plano_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		nr_seq_cobertura_w,
		nr_seq_tipo_cobertura_w,
		ie_tipo_atendimento_w,
		ie_sexo_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		select	count(1)
		into STRICT	qt_coberturas_w
		from	pls_cobertura
		where	nr_seq_contrato = nr_seq_contrato_p
		and	nr_seq_tipo_cobertura = nr_seq_tipo_cobertura_w
		and	ie_tipo_atendimento = ie_tipo_atendimento_w
		and	ie_sexo = ie_sexo_w;

		if (qt_coberturas_w = 0) then
			insert into pls_cobertura(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
					nm_usuario_nrec,nr_seq_contrato, nr_seq_tipo_cobertura, ie_tipo_atendimento,
					ie_sexo, ie_situacao, dt_inicio_vigencia, dt_fim_vigencia
					)
				(SELECT	nextval('pls_cobertura_seq'),clock_timestamp(), nm_usuario_p, dt_atualizacao_nrec,
					nm_usuario_nrec, nr_seq_contrato_p, nr_seq_tipo_cobertura, ie_tipo_atendimento,
					ie_sexo, ie_situacao, dt_inicio_vigencia, dt_fim_vigencia
				from	pls_cobertura
				where	nr_sequencia = nr_seq_cobertura_w);
		end if;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
