CREATE OR REPLACE PROCEDURE pls_copiar_competencia ( cd_estab_origem_p bigint, cd_estab_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_competencia_w		bigint;
nr_seq_competencia_ant_w	bigint;
qt_registro_w			bigint;

C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_competencia 
	where	cd_estabelecimento	= cd_estab_origem_p;


BEGIN 
open C01;
loop 
fetch C01 into	 
	nr_seq_competencia_ant_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	select	nextval('pls_competencia_seq') 
	into STRICT	nr_seq_competencia_w 
	;
	 
	select	count(*) 
	into STRICT	qt_registro_w 
	from	pls_competencia 
	where	cd_estabelecimento	= cd_estab_destino_p 
	and	nr_seq_anterior		= nr_seq_competencia_ant_w;
 
	if (qt_registro_w	= 0) then 
		insert into pls_competencia(nr_sequencia, cd_estabelecimento, dt_atualizacao, 
			nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, 
			dt_mes_competencia, dt_liberacao, nm_usuario_liberacao, 
			dt_fechamento, nm_usuario_fechamento, ds_maquina_liberacao, 
			ds_maquina_fechamento, vl_total_despesa, vl_total_imposto, nr_seq_anterior, 
			dt_mes_competencia_aux) 
		SELECT	nr_seq_competencia_w, cd_estab_destino_p, clock_timestamp(), 
			nm_usuario_p, clock_timestamp(), nm_usuario_p, 
			dt_mes_competencia, dt_liberacao, nm_usuario_liberacao, 
			dt_fechamento, nm_usuario_fechamento, ds_maquina_liberacao, 
			ds_maquina_fechamento, vl_total_despesa, vl_total_imposto, nr_seq_competencia_ant_w, 
			dt_mes_competencia_aux 
		from	pls_competencia 
		where	nr_sequencia	= nr_seq_competencia_ant_w;
	end if;
	end;
end loop;
close C01;
CALL pls_copiar_parametros(cd_estab_origem_p, cd_estab_destino_p, nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
