CREATE OR REPLACE PROCEDURE pls_copiar_dados_titular_simul ( nr_seq_simul_benef_p bigint, nr_seq_simul_titular_p bigint, ie_bonificacao_p text, ie_sca_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_vinculo_sca_w	bigint;
nr_seq_vinculo_bonif_w	bigint;
nr_seq_plano_sca_w	bigint;
nr_seq_tabela_w		bigint;
ds_erro_w		varchar(255);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_plano,
		nr_seq_tabela
	from	pls_sca_vinculo
	where	nr_seq_segurado_simul	= nr_seq_simul_titular_p
	and	ie_sca_p		= 'S'
	order by nr_sequencia;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_bonificacao_vinculo
	where	nr_seq_segurado_simul	= nr_seq_simul_titular_p
	and	ie_bonificacao_p	= 'S'
	order by nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_vinculo_sca_w,
	nr_seq_plano_sca_w,
	nr_seq_tabela_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_erro_w := pls_consistir_sca_simulacao(nr_seq_simul_benef_p, nr_seq_plano_sca_w, nr_seq_tabela_w, ds_erro_w);

	if (coalesce(ds_erro_w::text, '') = '') then
		insert into pls_sca_vinculo(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				nr_seq_segurado_simul,nr_seq_plano,nr_seq_tabela,nr_seq_vendedor_canal,nr_seq_vendedor_pf,
				qt_idade_limite,dt_inicio_vigencia)
			(SELECT	nextval('pls_sca_vinculo_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				nr_seq_simul_benef_p,nr_seq_plano,nr_seq_tabela,nr_seq_vendedor_canal,nr_seq_vendedor_pf,
				qt_idade_limite,dt_inicio_vigencia
			from	pls_sca_vinculo
			where	nr_sequencia	= nr_seq_vinculo_sca_w);
	end if;

	end;
end loop;
close C01;


open C02;
loop
fetch C02 into
	nr_seq_vinculo_bonif_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin

	insert into pls_bonificacao_vinculo(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
			nr_seq_segurado_simul,nr_seq_bonificacao,dt_inicio_vigencia,dt_fim_vigencia,tx_bonificacao,
			vl_bonificacao)
		(SELECT	nextval('pls_bonificacao_vinculo_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
			nr_seq_simul_benef_p,nr_seq_bonificacao,dt_inicio_vigencia,dt_fim_vigencia,tx_bonificacao,
			vl_bonificacao
		from	pls_bonificacao_vinculo
		where	nr_sequencia	= nr_seq_vinculo_bonif_w);

	end;
end loop;
close C02;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
