CREATE OR REPLACE PROCEDURE pls_copiar_diops_estab ( cd_estab_origem_p bigint, cd_estab_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_diops_w			bigint;
nr_seq_diops_ant_w		bigint;
ds_transacao_w			varchar(255);
ie_tipo_transacao_w		varchar(2);
dt_vigencia_w			timestamp;
qt_registro_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		dt_vigencia,
		ds_transacao,
		ie_tipo_transacao
	from	diops_transacao
	where	cd_estabelecimento	= cd_estab_origem_p;


BEGIN
open C01;
loop
fetch C01 into
	nr_seq_diops_ant_w,
	dt_vigencia_w,
	ds_transacao_w,
	ie_tipo_transacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	nextval('diops_transacao_seq')
	into STRICT	nr_seq_diops_w
	;

	select	count(*)
	into STRICT	qt_registro_w
	from	diops_transacao
	where	cd_estabelecimento	= cd_estab_destino_p
	and	nr_seq_anterior		= nr_seq_diops_ant_w;

	if (qt_registro_w	= 0) then
		insert into  diops_transacao(nr_sequencia, dt_atualizacao, nm_usuario,
			dt_atualizacao_nrec, nm_usuario_nrec, dt_vigencia,
			ds_transacao, ie_tipo_transacao, cd_estabelecimento, nr_seq_anterior)
		values (nr_seq_diops_w, clock_timestamp(), nm_usuario_p,
			clock_timestamp(), nm_usuario_p, dt_vigencia_w,
			ds_transacao_w, ie_tipo_transacao_w, cd_estab_destino_p, nr_seq_diops_ant_w);

		CALL pls_copiar_diops_conta(nr_seq_diops_w, nr_seq_diops_ant_w, cd_estab_destino_p, nm_usuario_p);
		CALL pls_copiar_diops_idade_saldo(nr_seq_diops_w, nr_seq_diops_ant_w, nm_usuario_p);
		CALL pls_copiar_diops_ans_solvencia(nr_seq_diops_w, nr_seq_diops_ant_w, nm_usuario_p);
	end if;
	end;
end loop;
close C01;


CALL pls_copiar_cargo_diops(cd_estab_origem_p, cd_estab_destino_p, nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
