CREATE OR REPLACE PROCEDURE pls_copiar_exce_copartic ( nr_seq_regra_copart_p pls_regra_coparticipacao.nr_sequencia%type, nr_seq_nova_regra_copart_p pls_regra_coparticipacao.nr_sequencia%type, dt_vigencia_p timestamp, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/*
	nr_seq_regra_copart_p - Regra de coparticipação na qual estão sendo copiadas as exceções
	nr_seq_nova_regra_copart_p - Regra de coparticipação na qual o registro duplicado de exceção deve pertencer. Pode ser a mesma que a
	regra origem, caso assim for necessário
*/
nr_nova_seq_w	pls_regra_copartic_exce.nr_sequencia%type;
dt_vigencia_w	timestamp;

C01 CURSOR FOR
	SELECT	nr_sequencia,
			coalesce( dt_inicio_vigencia, dt_vigencia_w ) dt_ini_vigencia,
			coalesce( dt_fim_vigencia, dt_vigencia_w ) dt_fim_vigencia
	from	pls_regra_copartic_exce
	where	nr_seq_regra_copartic = nr_seq_regra_copart_p;

BEGIN

	if (dt_vigencia_p IS NOT NULL AND dt_vigencia_p::text <> '') then
		dt_vigencia_w := dt_vigencia_p;
	else
		dt_vigencia_w := clock_timestamp();
	end if;

	for r_c01_w in C01 loop

		if ( r_c01_w.dt_ini_vigencia <= dt_vigencia_w  AND r_c01_w.dt_fim_vigencia >= dt_vigencia_w ) then

			nr_nova_seq_w := r_c01_w.nr_sequencia;
			nr_nova_seq_w := duplicar_registro('PLS_REGRA_COPARTIC_EXCE', nm_usuario_p, nr_nova_seq_w);

			EXECUTE 'update pls_regra_copartic_exce set nr_seq_regra_copartic = '||nr_seq_nova_regra_copart_p||'  where nr_sequencia = :nr_nova_seq_w'
			using nr_nova_seq_w;

		end if;

	end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
