CREATE OR REPLACE PROCEDURE pls_copiar_exig_caren_contrato ( nr_seq_contrato_p bigint, nr_seq_regra_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_regra_carencia_w	bigint;


BEGIN

select	nextval('pls_regra_carencia_isencao_seq')
into STRICT	nr_seq_regra_carencia_w
;

insert into pls_regra_carencia_isencao(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
		cd_estabelecimento,nr_seq_contrato,qt_dias_inicial,qt_dias_final,ie_tipo_data,
		ie_tipo_contratacao,qt_vidas_inicial,qt_vidas_final,ie_situacao,ie_exige_carencia,
		ie_mae_beneficiario,ie_acao_contrato,ie_responsavel_plano,qt_tempo_resp_plano,
		ie_regulamentacao,ie_referencia_vidas, qt_idade_minima, qt_idade_maxima)
	(SELECT	nr_seq_regra_carencia_w,clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
		cd_estabelecimento_p,nr_seq_contrato_p,qt_dias_inicial,qt_dias_final,ie_tipo_data,
		ie_tipo_contratacao,qt_vidas_inicial,qt_vidas_final,'A',ie_exige_carencia,
		ie_mae_beneficiario,ie_acao_contrato,ie_responsavel_plano,qt_tempo_resp_plano,
		ie_regulamentacao,ie_referencia_vidas, qt_idade_minima, qt_idade_maxima
	from	pls_regra_carencia_isencao
	where	nr_sequencia = nr_seq_regra_p);

insert into pls_regra_carencia_dias(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
		nr_seq_regra,nr_seq_tipo_carencia,qt_dias)
	(SELECT	nextval('pls_regra_carencia_dias_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
		nr_seq_regra_carencia_w,nr_seq_tipo_carencia,qt_dias
	from	pls_regra_carencia_dias
	where	nr_seq_regra	= nr_seq_regra_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
