CREATE OR REPLACE PROCEDURE pls_copiar_item_prod_regras ( nr_seq_item_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_apresentacao_w	bigint;


BEGIN

begin
select	max(nr_seq_apresentacao)
into STRICT	nr_seq_apresentacao_w
from	pls_item_prod_regra_lib
where	cd_perfil	= cd_perfil_p;
exception
when others then
	nr_seq_apresentacao_w	:= null;
end;

if (coalesce(nr_seq_apresentacao_w::text, '') = '') then
	nr_seq_apresentacao_w	:= 5;
else
	nr_seq_apresentacao_w	:= nr_seq_apresentacao_w + 5;
end if;

insert into pls_item_prod_regra_lib(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
		cd_estabelecimento,cd_perfil,nr_seq_item,nr_seq_apresentacao)
values (	nextval('pls_item_prod_regra_lib_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
		cd_estabelecimento_p,cd_perfil_p,nr_seq_item_p,nr_seq_apresentacao_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
