CREATE OR REPLACE PROCEDURE pls_copiar_mensagem_email_mens ( nm_usuario_p text, cd_perfil_p pls_mensagem_email.cd_perfil%type, ie_tipo_email_p bigint, nr_seq_mensagem_p INOUT bigint) AS $body$
DECLARE


ds_assunto_w		pls_mensagem_email.ds_assunto%type;
ds_mensagem_w		pls_mensagem_email.ds_mensagem%type;
nr_seq_mensagem_w	w_pls_envio_email.nr_sequencia%type;


BEGIN

delete from w_pls_envio_email
where	nm_usuario = nm_usuario_p
and	dt_atualizacao < (clock_timestamp() - interval '2 days');

ds_assunto_w	:= pls_gerar_mensagem_email(ie_tipo_email_p, 'A', nm_usuario_p, cd_perfil_p);
ds_mensagem_w	:= pls_gerar_mensagem_email(ie_tipo_email_p, 'ME', nm_usuario_p, cd_perfil_p);

if	((ie_tipo_email_p = 1) and ((coalesce(ds_assunto_w::text, '') = '') or (ds_assunto_w = ''))) then
	ds_assunto_w := obter_desc_expressao(646666);
end if;

select	nextval('w_pls_envio_email_seq')
into STRICT	nr_seq_mensagem_w
;

insert	into	w_pls_envio_email(nr_sequencia, ds_mensagem, ds_assunto,
	dt_atualizacao, dt_atualizacao_nrec, nm_usuario,
	nm_usuario_nrec)
values (nr_seq_mensagem_w, ds_mensagem_w, ds_assunto_w,
	clock_timestamp(), clock_timestamp(), nm_usuario_p,
	nm_usuario_p);

nr_seq_mensagem_p := nr_seq_mensagem_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
