SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_copiar_meta_equipe ( nr_seq_equipe_p bigint, nr_seq_meta_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_meta_atingir_w	bigint;
nr_seq_meta_atingir_ww	bigint;
nr_seq_meta_w		bigint;
nr_seq_meta_comissao_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_equipe_meta_atingir
	where	nr_seq_meta_equipe	= nr_seq_meta_p
	order by nr_sequencia;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_equipe_meta_comissao
	where	nr_seq_meta_atingir	= nr_seq_meta_atingir_w
	order by nr_sequencia;


BEGIN

select	nextval('pls_equipe_vend_meta_seq')
into STRICT	nr_seq_meta_w
;

insert into pls_equipe_vend_meta(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
		cd_estabelecimento,nr_seq_equipe,dt_inicio_vigencia,qt_meta,ie_tipo_contrato,
		nr_seq_vendedor_canal,ie_situacao_mensalidade,qt_dia_limite,dt_fim_vigencia,
		ie_tipo_meta, ds_observacao, nr_seq_grupo_produto, nr_seq_plano, ie_acao_contrato,
		ds_meta)
	(SELECT	nr_seq_meta_w,clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
		cd_estabelecimento_p,nr_seq_equipe_p,dt_inicio_vigencia,qt_meta,ie_tipo_contrato,
		nr_seq_vendedor_canal,ie_situacao_mensalidade,qt_dia_limite,dt_fim_vigencia,
		ie_tipo_meta, ds_observacao, nr_seq_grupo_produto, nr_seq_plano, ie_acao_contrato,
		ds_meta
	from	pls_equipe_vend_meta
	where	nr_sequencia	= nr_seq_meta_p);

open C01;
loop
fetch C01 into
	nr_seq_meta_atingir_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	nextval('pls_equipe_meta_atingir_seq')
	into STRICT	nr_seq_meta_atingir_ww
	;

	insert into pls_equipe_meta_atingir(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
			cd_estabelecimento,nr_seq_meta_equipe,pr_meta_minimo,pr_meta_maximo)
		(SELECT	nr_seq_meta_atingir_ww,clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
			cd_estabelecimento_p,nr_seq_meta_w,pr_meta_minimo,pr_meta_maximo
		from	pls_equipe_meta_atingir
		where	nr_sequencia	= nr_seq_meta_atingir_w);

	open C02;
	loop
	fetch C02 into
		nr_seq_meta_comissao_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		insert into pls_equipe_meta_comissao(	nr_sequencia,dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
				cd_estabelecimento, nr_seq_meta_atingir, qt_idade_inicial, qt_idade_final, pr_comissao,
				ie_acao_contrato, ie_tipo_item_mensalidade, nr_seq_plano_sca, vl_comissao,
				nr_seq_tipo_comercial, ie_portabilidade, nr_parcela_inicial, nr_parcela_final,
				nr_seq_motivo_inclusao, nr_seq_tipo_comissao, nr_parcela_contr_inicial, nr_parcela_contr_final,
				nr_seq_contrato, nr_contrato, dt_adesao_inicial, dt_adesao_final, dt_contrato_inicial, dt_contrato_final)
			(SELECT	nextval('pls_equipe_meta_comissao_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
				cd_estabelecimento_p, nr_seq_meta_atingir_ww, qt_idade_inicial, qt_idade_final, pr_comissao,
				ie_acao_contrato, ie_tipo_item_mensalidade, nr_seq_plano_sca, vl_comissao,
				nr_seq_tipo_comercial, ie_portabilidade, nr_parcela_inicial, nr_parcela_final,
				nr_seq_motivo_inclusao, nr_seq_tipo_comissao, nr_parcela_contr_inicial, nr_parcela_contr_final,
				nr_seq_contrato, nr_contrato, dt_adesao_inicial, dt_adesao_final, dt_contrato_inicial, dt_contrato_final
			from	pls_equipe_meta_comissao
			where	nr_sequencia	= nr_seq_meta_comissao_w);
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_copiar_meta_equipe ( nr_seq_equipe_p bigint, nr_seq_meta_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

