CREATE OR REPLACE PROCEDURE pls_copiar_municipios_regiao ( nr_seq_regiao_origem_p bigint, nr_seq_regiao_destino_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_municipio_ibge_w	varchar(6);
sg_uf_municipio_w	varchar(2);
qt_local_w		bigint;

C01 CURSOR FOR
	SELECT	cd_municipio_ibge,
		sg_uf_municipio
	from	pls_regiao_local
	where	nr_seq_regiao	= nr_seq_regiao_origem_p;


BEGIN

open C01;
loop
fetch C01 into
	cd_municipio_ibge_w,
	sg_uf_municipio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_local_w
	from	pls_regiao_local
	where	nr_seq_regiao		= nr_seq_regiao_destino_p
	and	cd_municipio_ibge	= cd_municipio_ibge_w
	and	sg_uf_municipio		= sg_uf_municipio_w;

	if (qt_local_w = 0) then
		insert	into	pls_regiao_local(	nr_sequencia, nr_seq_regiao, cd_municipio_ibge,
				sg_uf_municipio, dt_atualizacao, nm_usuario,
				dt_atualizacao_nrec, nm_usuario_nrec)
			values (	nextval('pls_regiao_local_seq'), nr_seq_regiao_destino_p, cd_municipio_ibge_w,
				sg_uf_municipio_w, clock_timestamp(), nm_usuario_p,
				clock_timestamp(), nm_usuario_p);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
