CREATE OR REPLACE PROCEDURE pls_copiar_plano_lista ( nr_seq_plano_origem_p bigint, nr_seq_plano_destino_p bigint, ie_excluir_p text, ds_lista_opcao_p text, nm_usuario_p text, ds_erro_p INOUT text, qt_copiadas_p INOUT bigint, qt_com_erro_p INOUT bigint) AS $body$
DECLARE


lista_opcoes_w		varchar(1000);
tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
ie_opcao_w		smallint;
ie_contador_w		smallint;


BEGIN

lista_opcoes_w	:= ds_lista_opcao_p;
qt_copiadas_p	:= 0;
qt_com_erro_p	:= 0;
ie_contador_w	:= 0;

while	(lista_opcoes_w IS NOT NULL AND lista_opcoes_w::text <> '') and
	ie_contador_w < 200 loop
	begin
	tam_lista_w		:= length(lista_opcoes_w);
	ie_pos_virgula_w	:= position(',' in lista_opcoes_w);

	if (ie_pos_virgula_w <> 0) then
		ie_opcao_w	:= substr(lista_opcoes_w, 1, (ie_pos_virgula_w - 1));
		lista_opcoes_w	:= substr(lista_opcoes_w, (ie_pos_virgula_w + 1), tam_lista_w);

		ds_erro_p := pls_copiar_plano(
			nr_seq_plano_origem_p, nr_seq_plano_destino_p, ie_excluir_p, ie_opcao_w, nm_usuario_p, ds_erro_p);

		qt_copiadas_p	:= qt_copiadas_p + 1;
		if (ds_erro_p <> '') then
			qt_com_erro_p	:= qt_com_erro_p + 1;
		end if;
	end if;

	ie_contador_w	:= ie_contador_w + 1;
	end;
end loop;

commit;

end;
$body$
