CREATE OR REPLACE PROCEDURE pls_copiar_produtos_interc ( nr_sequencia_p bigint, cd_plano_intercambio_p text, nm_usuario_p text) AS $body$
DECLARE


ie_abrangencia_w		varchar(2);
ie_abrangencia_copia_w		varchar(2);
ie_tipo_contratacao_w		varchar(2);
ie_tipo_contratacao_copia_w	varchar(2);
qt_registros_w			bigint;

C01 CURSOR FOR
	SELECT	vl_dominio
	from	valor_dominio
	where	cd_dominio	= 1666;

C02 CURSOR FOR
	SELECT	vl_dominio
	from	valor_dominio
	where	cd_dominio	= 1667;


BEGIN

if (cd_plano_intercambio_p IS NOT NULL AND cd_plano_intercambio_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '')	then
	open C01;
	loop
	fetch C01 into
		ie_tipo_contratacao_copia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		open C02;
		loop
		fetch C02 into
			ie_abrangencia_copia_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin

			select	count(*)
			into STRICT	qt_registros_w
			from	pls_plano
			where	ie_tipo_operacao	= 'T'
			and	cd_plano_intercambio	= cd_plano_intercambio_p
			and	ie_tipo_contratacao	= ie_tipo_contratacao_copia_w
			and	ie_abrangencia		= ie_abrangencia_copia_w;

			if (qt_registros_w	= 0) then
				insert into pls_plano(cd_codigo_ant,
					cd_codigo_carteira,
					cd_estabelecimento,
					cd_plano,
					cd_plano_intercambio,
					cd_scpa,
					cd_tarja_magnetica,
					ds_carteira_acomodacao,
					ds_combinacao_opcionais,
					ds_descricao_plano,
					ds_plano,
					dt_ativacao,
					dt_atualizacao,
					dt_atualizacao_nrec,
					dt_cancelamento,
					dt_liberacao,
					dt_reajuste,
					dt_suspensao,
					ie_abrangencia,
					ie_acomodacao,
					ie_coparticipacao,
					ie_destaque_intercambio,
					ie_franquia,
					ie_grau_dependencia,
					ie_lancamento_mensalidade,
					ie_livre_escolha,
					ie_participacao,
					ie_pcmso,
					ie_preco,
					ie_proposta_adesao,
					ie_rede_exclusiva,
					ie_regulamentacao,
					ie_segmentacao,
					ie_seguro_obito,
					ie_sem_vinculo,
					ie_simulacao_preco,
					ie_situacao,
					ie_tipo_contratacao,
					ie_tipo_operacao,
					ie_tipo_produto,
					ie_utilizacao_plano,
					ie_vinculo_ativo,
					ie_vinculo_inativo,
					nm_fantasia,
					nm_usuario,
					nm_usuario_nrec,
					nr_protocolo_ans,
					nr_seq_apres,
					nr_seq_classificacao,
					nr_seq_congenere,
					nr_seq_outorgante,
					nr_seq_plano_ant,
					nr_sequencia,
					qt_idade_sca,
					qt_parcelas_cobranca)
				SELECT	cd_codigo_ant,
					cd_codigo_carteira,
					cd_estabelecimento,
					cd_plano,
					cd_plano_intercambio,
					cd_scpa,
					cd_tarja_magnetica,
					ds_carteira_acomodacao,
					ds_combinacao_opcionais,
					ds_descricao_plano,
					ds_plano,
					dt_ativacao,
					clock_timestamp(),
					clock_timestamp(),
					dt_cancelamento,
					dt_liberacao,
					dt_reajuste,
					dt_suspensao,
					ie_abrangencia_copia_w,
					ie_acomodacao,
					ie_coparticipacao,
					ie_destaque_intercambio,
					ie_franquia,
					ie_grau_dependencia,
					ie_lancamento_mensalidade,
					ie_livre_escolha,
					ie_participacao,
					ie_pcmso,
					ie_preco,
					ie_proposta_adesao,
					ie_rede_exclusiva,
					ie_regulamentacao,
					ie_segmentacao,
					ie_seguro_obito,
					ie_sem_vinculo,
					ie_simulacao_preco,
					ie_situacao,
					ie_tipo_contratacao_copia_w,
					ie_tipo_operacao,
					ie_tipo_produto,
					ie_utilizacao_plano,
					ie_vinculo_ativo,
					ie_vinculo_inativo,
					nm_fantasia,
					nm_usuario_p,
					nm_usuario_p,
					nr_protocolo_ans,
					nr_seq_apres,
					nr_seq_classificacao,
					nr_seq_congenere,
					nr_seq_outorgante,
					nr_seq_plano_ant,
					nextval('pls_plano_seq'),
					qt_idade_sca,
					qt_parcelas_cobranca
				from	pls_plano
				where	nr_sequencia = nr_sequencia_p;
			end if;
			end;
		end loop;
		close C02;
		end;
	end loop;
	close C01;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
