CREATE OR REPLACE PROCEDURE pls_copiar_regras_vendedor ( ds_lista_check_regra_p text, nr_sequencia_p bigint, ie_tipo_guia_p text, nm_usuario_p text) AS $body$
DECLARE


ds_lista_check_regra_w	varchar(2000);
nr_pos_virgula_w	bigint;
nr_seq_sequencia_w	bigint;
nr_controle_loop_w	smallint := 0;


BEGIN
if (ds_lista_check_regra_p IS NOT NULL AND ds_lista_check_regra_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_tipo_guia_p IS NOT NULL AND ie_tipo_guia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	ds_lista_check_regra_w := ds_lista_check_regra_p;

	while(ds_lista_check_regra_w IS NOT NULL AND ds_lista_check_regra_w::text <> '') and (nr_controle_loop_w < 100) loop
		begin
		nr_pos_virgula_w := position(',' in ds_lista_check_regra_w);

		if (nr_pos_virgula_w > 0) then
			begin
			nr_seq_sequencia_w	:= (substr(ds_lista_check_regra_w,0,nr_pos_virgula_w-1))::numeric;
			ds_lista_check_regra_w	:= substr(ds_lista_check_regra_w,nr_pos_virgula_w+1,length(ds_lista_check_regra_w));
			end;
		else
			begin
			nr_seq_sequencia_w	:= (ds_lista_check_regra_w)::numeric;
			ds_lista_check_regra_w	:= null;
			end;
		end if;

		if (nr_seq_sequencia_w > 0) then
			begin
			CALL pls_copiar_regra_comissao(
					nr_seq_sequencia_w,
					nr_sequencia_p,
					ie_tipo_guia_p,
					nm_usuario_p);
			end;
		end if;
		nr_controle_loop_w := nr_controle_loop_w + 1;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
