CREATE OR REPLACE PROCEDURE pls_copiar_tuss_tab_prec_prest (nr_seq_prestador_p bigint, cd_edicao_amb_p bigint, dt_inicio_vigencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text, ie_somente_vigentes_p text) AS $body$
DECLARE


qt_registro_w			bigint;
ie_origem_proced_w		bigint;
cd_procedimento_w		bigint;
dt_vigencia_final_w		preco_tuss.dt_final_vigencia%type;

C01 CURSOR FOR
	SELECT	a.ie_origem_proced,
		a.cd_procedimento,
		a.dt_final_vigencia
	from	preco_tuss	a,
		edicao_amb	b
	where	a.cd_edicao_amb = b.cd_edicao_amb
	and	a.cd_edicao_amb  = cd_edicao_amb_p
	and	b.cd_estabelecimento = cd_estabelecimento_p;


BEGIN

if (cd_edicao_amb_p IS NOT NULL AND cd_edicao_amb_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		ie_origem_proced_w,
		cd_procedimento_w,
		dt_vigencia_final_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	count(1)
		into STRICT	qt_registro_w
		from	pls_prestador_proc
		where	nr_seq_prestador = nr_seq_prestador_p
		and	ie_origem_proced = ie_origem_proced_w
		and	cd_procedimento	 = cd_procedimento_w;


		if (qt_registro_w = 0) then

			if (ie_somente_vigentes_p = 'N') or
				((coalesce(dt_vigencia_final_w::text, '') = '') or (dt_vigencia_final_w > clock_timestamp())) then

					insert into pls_prestador_proc(nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_prestador,
						cd_procedimento,
						ie_origem_proced,
						ie_liberar,
						dt_inicio_vigencia,
						ie_internado)
					values (nextval('pls_prestador_proc_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						nr_seq_prestador_p,
						cd_procedimento_w,
						ie_origem_proced_w,
						'S',
						dt_inicio_vigencia_p,
						'N');
			end if;
		end if;
		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
