SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_copias_dados_familiar ( cd_pessoa_fisica_p text, ie_tipo_escolha_p text) AS $body$
DECLARE


cd_cep_w		varchar(15);
ds_endereco_w		varchar(100);
nr_endereco_w		integer;
ds_bairro_w		varchar(80);
ds_municipio_w		varchar(40);
sg_estado_w		compl_pessoa_fisica.sg_estado%type;
nr_telefone_w		varchar(15);
nr_ddd_telefone_w	varchar(3);
cd_pessoa_fisica_dep_w	varchar(40);
nr_seq_segurado_w	bigint;
ds_complemento_w	varchar(40);
ds_fone_adic_w		varchar(80);
cd_municipio_ibge_w	varchar(6);
ie_restringir_copia_w	varchar(1);
dt_rescisao_w		timestamp;

C00 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_segurado
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

C01 CURSOR FOR
	SELECT	cd_pessoa_fisica,
		dt_rescisao
	from	pls_segurado
	where	nr_seq_titular	= nr_seq_segurado_w;


BEGIN

ie_restringir_copia_w	:= coalesce(obter_valor_param_usuario(1220, 46, Obter_Perfil_Ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento), 'N');

begin
	select	coalesce(cd_cep,''),
		coalesce(ds_endereco,''),
		coalesce(nr_endereco,''),
		coalesce(ds_bairro,''),
		coalesce(ds_municipio,''),
		coalesce(sg_estado,''),
		coalesce(nr_telefone,''),
		coalesce(nr_ddd_telefone,''),
		coalesce(ds_complemento,''),
		coalesce(ds_fone_adic,''),
		coalesce(cd_municipio_ibge,'')
	into STRICT	cd_cep_w,
		ds_endereco_w,
		nr_endereco_w,
		ds_bairro_w,
		ds_municipio_w,
		sg_estado_w,
		nr_telefone_w,
		nr_ddd_telefone_w,
		ds_complemento_w,
		ds_fone_adic_w,
		cd_municipio_ibge_w
	from	compl_pessoa_fisica
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	ie_tipo_complemento	= 1;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(223523);
end;


open C00;
loop
fetch C00 into
	nr_seq_segurado_w;
EXIT WHEN NOT FOUND; /* apply on C00 */
	begin

	open C01;
	loop
	fetch C01 into
		cd_pessoa_fisica_dep_w,
		dt_rescisao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (ie_restringir_copia_w = 'N') or (coalesce(dt_rescisao_w::text, '') = '') then
			if (ie_tipo_escolha_p = 'E') then
				update	compl_pessoa_fisica
				set	cd_cep			= cd_cep_w,
					ds_endereco		= ds_endereco_w,
					nr_endereco		= nr_endereco_w,
					ds_bairro		= ds_bairro_w,
					ds_municipio		= ds_municipio_w,
					sg_estado		= sg_estado_w,
					ds_complemento		= ds_complemento_w,
					cd_municipio_ibge	= cd_municipio_ibge_w,
					dt_atualizacao		= clock_timestamp(),
					nm_usuario		= wheb_usuario_pck.get_nm_usuario
				where	cd_pessoa_fisica	= cd_pessoa_fisica_dep_w
				and	ie_tipo_complemento	in (1,2,7);
			elsif (ie_tipo_escolha_p = 'T') then
				update	compl_pessoa_fisica
				set	nr_telefone		= nr_telefone_w,
					nr_ddd_telefone		= nr_ddd_telefone_w,
					ds_fone_adic		= ds_fone_adic_w,
					dt_atualizacao		= clock_timestamp(),
					nm_usuario		= wheb_usuario_pck.get_nm_usuario
				where	cd_pessoa_fisica	= cd_pessoa_fisica_dep_w
				and	ie_tipo_complemento	in (1,2,7);
			end if;
		end if;
		end;
	end loop;
	close C01;
	end;
end loop;
close C00;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_copias_dados_familiar ( cd_pessoa_fisica_p text, ie_tipo_escolha_p text) FROM PUBLIC;

