CREATE OR REPLACE PROCEDURE pls_copia_preco_reemb_lista ( nr_seq_prest_origem_p bigint, dt_inicio_vigencia_p timestamp, ds_lista_tabela_p text, ie_excluir_p text, nm_usuario_p text, ds_erro_p INOUT text, qt_copiadas_p INOUT bigint, qt_com_erro_p INOUT bigint) AS $body$
DECLARE

 
lista_tabelas_w		varchar(255);
tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
ie_tabela_w		smallint;
ie_contador_w		smallint;
nm_tabela_w		varchar(50);
ds_saida_w		varchar(1);


BEGIN 
if (nr_seq_prest_origem_p IS NOT NULL AND nr_seq_prest_origem_p::text <> '') and (dt_inicio_vigencia_p IS NOT NULL AND dt_inicio_vigencia_p::text <> '') and (ds_lista_tabela_p IS NOT NULL AND ds_lista_tabela_p::text <> '') and (ie_excluir_p IS NOT NULL AND ie_excluir_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	lista_tabelas_w	:= ds_lista_tabela_p;
	qt_copiadas_p	:= 0;
	qt_com_erro_p	:= 0;
	ie_contador_w	:= 0;
 
	while(lista_tabelas_w IS NOT NULL AND lista_tabelas_w::text <> '') and (ie_contador_w < 200) loop 
		begin 
		tam_lista_w		:= length(lista_tabelas_w);
		ie_pos_virgula_w	:= position(',' in lista_tabelas_w);
 
		if (ie_pos_virgula_w <> 0) then 
			begin 
			ie_tabela_w	:= (substr(lista_tabelas_w, 1, (ie_pos_virgula_w - 1)))::numeric;
			lista_tabelas_w	:= substr(lista_tabelas_w, (ie_pos_virgula_w + 1), tam_lista_w);
			 
			if (ie_tabela_w = 0) then 
				begin 
				nm_tabela_w := 'PLS_REGRA_PRECO_PROC';
				end;
			elsif (ie_tabela_w = 1) then 
				begin 
				nm_tabela_w := 'PLS_REGRA_PRECO_MAT';
				end;
			elsif (ie_tabela_w = 2) then 
				begin 
				nm_tabela_w := 'PLS_REGRA_PRECO_SERVICO';
				end;
			end if;
 
			ds_saida_w := pls_copia_preco_reembolso( 
				nr_seq_prest_origem_p, dt_inicio_vigencia_p, nm_tabela_w, ie_excluir_p, nm_usuario_p, ds_saida_w);
 
			qt_copiadas_p := coalesce(qt_copiadas_p,0) + 1;
			end;
		end if;
		ie_contador_w := ie_contador_w + 1;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
