SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_criar_reg_contab_taxa_adic () AS $body$
DECLARE

	 
	 
C01 CURSOR FOR 
	SELECT	x.nr_sequencia nr_seq_conta 
	from	pls_conta	x 
	where	ie_status	= 'F' 
	and	exists (	SELECT	1 
					from	pls_conta_pos_estab_taxa	a, 
						pls_conta_pos_estabelecido	b 
					where	a.nr_seq_conta_pos_estab	= b.nr_sequencia 
					and	b.nr_seq_conta			= x.nr_sequencia) 
	and	not exists (	select	1 
					from	pls_conta_pos_estab_taxa	a, 
						pls_conta_pos_estabelecido	b, 
						pls_conta_pos_taxa_contab	c 
					where	a.nr_seq_conta_pos_estab	= b.nr_sequencia 
					and	c.nr_seq_pos_estab_taxa		= a.nr_sequencia 
					and	b.nr_seq_conta			= x.nr_sequencia);

BEGIN 
 
for r_c01_w in c01 loop 
	 
	CALL pls_gerar_contab_taxa_val_adic(r_c01_w.nr_seq_conta,'TASY');
	 
	if (mod(c01%rowCount,1000) = 0) then 
		commit;
	end if;
end loop;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
