CREATE OR REPLACE PROCEDURE pls_cria_evento_cid ( nm_usuario_p text) AS $body$
DECLARE

qt_registro_w		bigint;
nr_seq_motivo_glosa_w	bigint;

BEGIN

	select 	max(a.nr_sequencia)
	into STRICT	nr_seq_motivo_glosa_w
	from	pls_glosa_evento	b,
		tiss_motivo_glosa	a
	where	a.nr_sequencia	= b.nr_seq_motivo_glosa
	and	a.cd_motivo_tiss = 1509;

	if (coalesce(nr_seq_motivo_glosa_w,0) > 0) then
		insert into pls_glosa_evento(	nr_sequencia, dt_atualizacao, nm_usuario,
						dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_motivo_glosa,
						ie_evento,ie_plano)
				values (	nextval('pls_glosa_evento_seq'), clock_timestamp(), nm_usuario_p,
						clock_timestamp(), nm_usuario_p, nr_seq_motivo_glosa_w,
						'CC', 'N');
	end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
