SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cria_job_ajus_estr_mat () AS $body$
DECLARE


jobno			bigint;
qt_reg_w		bigint;
ds_comando_job_w	varchar(2000);	


BEGIN

-- verifica se existem jobs ativas, se tiver alguma job parada j?? faz a limpeza da mesma
qt_reg_w := pls_obter_qt_job_ativa('PLS_AJUSTAR_ESTRUT_MAT_TM', qt_reg_w);

if (qt_reg_w = 0) then
	begin
		ds_comando_job_w	:= 'PLS_AJUSTAR_ESTRUT_MAT_TM;';
		DBMS_JOB.SUBMIT(job => jobno,
				what => ds_comando_job_w,
				next_date => clock_timestamp());
		commit;
	exception
	when others then
		null;
	end;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1093644); -- O processo já está em execução.
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
