CREATE OR REPLACE PROCEDURE pls_cta_gerar_job_regra ( ie_acao_p text, ds_rotina_p text, ds_execucao_p text, dt_execucao_p timestamp, dt_proxima_exec_p text, ie_situacao_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

Alterações:		ROTINA CHAMADA DENTRO DE TRIGGER
-------------------------------------------------------------------------------------------------------------------
	ie_acao_p	I - Insert	U - Update	D - Delete
*/
jobno 			integer;
nr_mes_w		integer;
ds_rotina_w		varchar(255);
ds_execucao_w		varchar(255);

-- transformado o select em um cursor para que o mesmo possa eliminar tudo
c01 CURSOR(	ds_rotina_pc	text) FOR
	SELECT	job
	from	all_jobs
	where	what like '%'||ds_rotina_pc||'%';
BEGIN
-- inicia as variáveis e faz o tratamento nos campos
ds_rotina_w := substr(upper(ds_rotina_p), 1, 255);
ds_execucao_w := substr(upper(ds_execucao_p), 1, 255);

-- se for inserção ou alteração
if (ie_acao_p = 'I') or (ie_acao_p = 'U') then

	if (ie_acao_p = 'U') then

		for r_c01_w in c01(ds_rotina_w) loop

			if (r_c01_w.job IS NOT NULL AND r_c01_w.job::text <> '') then
				dbms_job.remove(r_c01_w.job);
			end if;
		end loop;
	end if;

	-- não retirar este if, o mesmo é verificado na chamada feita em uma regra que nela é informado se
	-- está ativa ou inativa, por este motivo não deve ser retirado
	if (ie_situacao_p = 'A') then
		dbms_job.submit( jobno, ds_execucao_w, dt_execucao_p, dt_proxima_exec_p);
	end if;
-- quando for para excluir
elsif (ie_acao_p = 'D') then

	for r_c01_w in c01(ds_rotina_w) loop
		if (r_c01_w.job IS NOT NULL AND r_c01_w.job::text <> '') then
			dbms_job.remove(r_c01_w.job);
		end if;
	end loop;
end if;

-- o commit deve ser feito na rotina que está chamando esta procedure
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
