SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_dados_cd_prestador_web (cd_prestador_p text, nr_seq_usu_web_p bigint, nr_seq_prestador_p INOUT bigint, nm_prestador_p INOUT text) AS $body$
DECLARE

				 
nm_prestador_w		varchar(80) := null;
nr_seq_prestador_w	bigint := null;
cd_prestador_w		varchar(30) := null;
nr_seq_usu_web_w	bigint := null;


BEGIN 
 
nr_seq_prestador_w 	:= nr_seq_prestador_p;
cd_prestador_w		:= cd_prestador_p;
nr_seq_usu_web_w	:= nr_seq_usu_web_p;
 
if (cd_prestador_w IS NOT NULL AND cd_prestador_w::text <> '') then 
	select	max(b.nr_sequencia) 
	into STRICT	nr_seq_prestador_w 
	from	pls_prestador_usuario_web a, 
		pls_prestador b 
	where	a.nr_seq_prestador = b.nr_sequencia 
	and	a.nr_seq_usuario = coalesce(nr_seq_usu_web_w,a.nr_seq_usuario) 
	and	b.cd_prestador = cd_prestador_w 
	and	a.ie_situacao = 'A';
	 
	if (nr_seq_prestador_w IS NOT NULL AND nr_seq_prestador_w::text <> '') then 
		select	max(nm_interno) 
		into STRICT	nm_prestador_w 
		from	pls_prestador 
		where	nr_sequencia = nr_seq_prestador_w;
		 
		if (coalesce(nm_prestador_w::text, '') = '') then 
			select	substr(obter_nome_pf_pj(cd_pessoa_fisica,cd_cgc),1,255) 
			into STRICT 	nm_prestador_w 
			from	pls_prestador 
			where	nr_sequencia = nr_seq_prestador_w;	
		end if;	
	end if;
end if;
	 
nm_prestador_p 		:= nm_prestador_w;
nr_seq_prestador_p 	:= nr_seq_prestador_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
