SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_dados_intercambio_web ( nr_seq_guia_p bigint, nr_seq_req_p bigint, cd_estabelecimento_p bigint, ie_transacao_p INOUT text, cd_cooperativa_p INOUT text, nm_cooperativa_p INOUT text, ie_tipo_intercambio_p INOUT text, ie_estagio_req_p INOUT bigint) AS $body$
DECLARE

 
/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade:  Retornar os dados do intercâmbio 
---------------------------------------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [ ] Relatórios [ ] Outros: 
 ---------------------------------------------------------------------------------------------------------------------------------------------------- 
Pontos de atenção:  
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/				 
				 
cd_usuario_plano_w		varchar(30);
nr_seq_emissor_w		bigint;
ie_tipo_trans_w			varchar(4);
cd_operadora_usuario_w		varchar(10);
cd_cooperativa_w		varchar(10)	:= '';
ie_tipo_intercambio_w		varchar(2)	:= 'I';
nr_seq_guia_proc_w		bigint;
nr_seq_req_proc_w		bigint;
nr_seq_regra_lib_w		bigint;
ie_item_lib_w			varchar(2)	:= 'N';
ie_itens_pendentes_w		varchar(2);
nr_seq_segurado_w		bigint;
nr_seq_congenere_benef_w	bigint;
ie_estagio_req_w		smallint;

C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_guia_plano_proc 
	where	nr_seq_guia	= nr_seq_guia_p;

C02 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_requisicao_proc 
	where	nr_seq_requisicao	= nr_seq_req_p;


BEGIN 
 
if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then 
	select	substr(pls_obter_dados_segurado(nr_seq_segurado,'C'),1,255), 
		pls_obter_dados_segurado(nr_seq_segurado,'NRCON'), 
		nr_seq_segurado 
	into STRICT	cd_usuario_plano_w, 
		nr_seq_congenere_benef_w, 
		nr_seq_segurado_w 
	from	pls_guia_plano 
	where	nr_sequencia = nr_seq_guia_p;
elsif (nr_seq_req_p IS NOT NULL AND nr_seq_req_p::text <> '') then 
	select	substr(pls_obter_dados_segurado(nr_seq_segurado,'C'),1,255), 
		pls_obter_dados_segurado(nr_seq_segurado,'NRCON'), 
		nr_seq_segurado, 
		ie_estagio 
	into STRICT	cd_usuario_plano_w, 
		nr_seq_congenere_benef_w, 
		nr_seq_segurado_w, 
		ie_estagio_req_w 
	from	pls_requisicao 
	where	nr_sequencia = nr_seq_req_p;
end if;
 
 
if ((cd_usuario_plano_w IS NOT NULL AND cd_usuario_plano_w::text <> '') and length(cd_usuario_plano_w) >= 17) then 
	select	max(nr_seq_emissor) 
	into STRICT	nr_seq_emissor_w 
	from	pls_parametros 
	where	cd_estabelecimento	= cd_estabelecimento_p;
	 
	ie_tipo_trans_w	:= pls_obter_tipo_processo_web(nr_seq_guia_p, nr_seq_req_p, cd_estabelecimento_p);
	 
	if (ie_tipo_trans_w	= 'I') then 
		select	coalesce(max(b.ie_tipo_intercambio), 'I')	 
		into STRICT	ie_tipo_intercambio_w 
		from	pls_cong_autor_intercambio b, 
			pls_congenere a 
		where	a.nr_sequencia 			= b.nr_seq_congenere 
		and	a.nr_sequencia			= nr_seq_congenere_benef_w;
		 
		if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then 
			ie_itens_pendentes_w := pls_atualiza_estag_guia_interc(nr_seq_guia_p, null, ie_itens_pendentes_w);
			 
			if (ie_itens_pendentes_w	= 'N') then 
				ie_tipo_intercambio_w	:= 'A';
			end if;
		end if;	
	end if;
end if;
 
-- Se o processo for intercâmbio e vier o estágio aprovado, deve mudar para aguardando envio. Situação ocorre nos casos de fundação (Unimed Blumenau). 
if (ie_tipo_trans_w = 'I' and (nr_seq_req_p IS NOT NULL AND nr_seq_req_p::text <> '')) and (ie_estagio_req_w in (2,6)) then 
	ie_tipo_intercambio_w	:= 'D';
	ie_estagio_req_w	:= 10;
end if;
 
ie_transacao_p		:= 	ie_tipo_trans_w;
cd_cooperativa_p	:=	cd_cooperativa_w;
ie_tipo_intercambio_p	:= 	ie_tipo_intercambio_w;
ie_estagio_req_p	:=   ie_estagio_req_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_dados_intercambio_web ( nr_seq_guia_p bigint, nr_seq_req_p bigint, cd_estabelecimento_p bigint, ie_transacao_p INOUT text, cd_cooperativa_p INOUT text, nm_cooperativa_p INOUT text, ie_tipo_intercambio_p INOUT text, ie_estagio_req_p INOUT bigint) FROM PUBLIC;

