SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_deletar_co ( nr_seq_conta_p bigint, nr_seq_conta_co_p bigint, ie_commit_p text, nm_usuario_p text, cd_estabelecimento_p text) AS $body$
DECLARE


qt_lote_liberacao_w		integer;
nr_seq_conta_w			pls_conta_coparticipacao.nr_seq_conta%type;
nr_seq_conta_proc_w		pls_conta_coparticipacao.nr_seq_conta_proc%type;
nr_seq_conta_mat_w		pls_conta_coparticipacao.nr_seq_conta_mat%type;
nr_seq_mensalidade_seg_w	pls_conta_co.nr_seq_mensalidade_seg%type;
nr_seq_regra_limite_mens_w	pls_conta_co.nr_seq_regra_limite_mens%type;
qt_registros_mensalidade_w	bigint;
nr_seq_conta_co_w		pls_conta_co.nr_sequencia%type;

BEGIN
-- Se passar o numero da custo operacional, ele só vai deletar aquele custo, se apenas passar o numero da conta ele vai deletar todos custos da conta
if (nr_seq_conta_co_p IS NOT NULL AND nr_seq_conta_co_p::text <> '') then
	select	max(nr_seq_mensalidade_seg),
		max(nr_seq_regra_limite_mens),
		max(nr_sequencia)
	into STRICT	nr_seq_mensalidade_seg_w,
		nr_seq_regra_limite_mens_w,
		nr_seq_conta_co_w
	from	pls_conta_co
	where	nr_sequencia	= nr_seq_conta_co_p;

	if	((coalesce(nr_seq_mensalidade_seg_w::text, '') = '') and (coalesce(nr_seq_regra_limite_mens_w::text, '') = '')) then
		select  count(1)
		into STRICT 	qt_registros_mensalidade_w
		from  	pls_mensalidade_item_conta a
		where  	a.nr_seq_conta_co = nr_seq_conta_co_p;
	else
		qt_registros_mensalidade_w	:= 0;
	end if;

	if	((nr_seq_mensalidade_seg_w IS NOT NULL AND nr_seq_mensalidade_seg_w::text <> '') or (nr_seq_regra_limite_mens_w IS NOT NULL AND nr_seq_regra_limite_mens_w::text <> '') or (qt_registros_mensalidade_w > 0)) and (nr_seq_conta_co_w IS NOT NULL AND nr_seq_conta_co_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(334824); --Já existe mensalidade para o custo operacional. Favor verificar.
	else
		select	count(1)
		into STRICT	qt_lote_liberacao_w
		from	pls_lib_pos_estabelecido
		where	nr_seq_conta_co	= nr_seq_conta_co_p;

		if (qt_lote_liberacao_w > 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(334825); --Já existe lote de liberação para o custo operacional. Favor verificar na função OPS - Controle de Coparticipações e Pós-estabelecidos.
		else
			select	nr_seq_conta_mat,
				nr_seq_conta_proc
			into STRICT	nr_seq_conta_mat_w,
				nr_seq_conta_proc_w
			from	pls_conta_co
			where	nr_sequencia = nr_seq_conta_co_p;

			delete	FROM pls_conta_mat_aprop
			where	nr_seq_conta_mat = nr_seq_conta_mat_w;

			delete	FROM pls_conta_proc_aprop
			where	nr_seq_conta_proc = nr_seq_conta_proc_w;

			delete	FROM pls_conta_co_aprop
			where	nr_seq_conta_co	= nr_seq_conta_co_p;

			delete	FROM pls_conta_co
			where	nr_sequencia	= nr_seq_conta_co_p;
		end if;
	end if;
elsif (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then
	select	max(nr_seq_mensalidade_seg),
		max(nr_seq_regra_limite_mens),
		max(nr_sequencia)
	into STRICT	nr_seq_mensalidade_seg_w,
		nr_seq_regra_limite_mens_w,
		nr_seq_conta_co_w
	from	pls_conta_co
	where	nr_seq_conta	= nr_seq_conta_p;

	if	((coalesce(nr_seq_mensalidade_seg_w::text, '') = '') and (coalesce(nr_seq_regra_limite_mens_w::text, '') = '')) and (nr_seq_conta_co_w IS NOT NULL AND nr_seq_conta_co_w::text <> '') then
		select  count(1)
		into STRICT 	qt_registros_mensalidade_w
		from  	pls_mensalidade_seg_item b
		where  	b.nr_seq_conta = nr_seq_conta_p;
	else
		qt_registros_mensalidade_w	:= 0;
	end if;

	if	((nr_seq_mensalidade_seg_w IS NOT NULL AND nr_seq_mensalidade_seg_w::text <> '') or (nr_seq_regra_limite_mens_w IS NOT NULL AND nr_seq_regra_limite_mens_w::text <> '') or (qt_registros_mensalidade_w > 0)) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(334824); --Já existe mensalidade para o custo operacional. Favor verificar.
	else
		select	count(1)
		into STRICT	qt_lote_liberacao_w
		from	pls_lib_pos_estabelecido
		where	nr_seq_conta	= nr_seq_conta_p;

		if (qt_lote_liberacao_w > 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(334825); --Já existe lote de liberação para o custo operacional. Favor verificar na função OPS - Controle de Coparticipações e Pós-estabelecidos.
		else
			delete	FROM pls_conta_co_aprop
			where	nr_seq_conta_co in (	SELECT  nr_sequencia
							from	pls_conta_co
							where	nr_seq_conta  = nr_seq_conta_p);

			delete	FROM pls_conta_mat_aprop
			where	nr_seq_conta_mat in (	SELECT	nr_sequencia
							from	pls_conta_mat
							where	nr_seq_conta  = nr_seq_conta_p);

			delete	FROM pls_conta_proc_aprop
			where	nr_seq_conta_proc in (	SELECT	nr_sequencia
							from	pls_conta_proc
							where	nr_seq_conta  = nr_seq_conta_p);

			delete	FROM pls_conta_co
			where	nr_seq_conta	= nr_seq_conta_p;
		end if;
	end if;
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_deletar_co ( nr_seq_conta_p bigint, nr_seq_conta_co_p bigint, ie_commit_p text, nm_usuario_p text, cd_estabelecimento_p text) FROM PUBLIC;

