CREATE OR REPLACE PROCEDURE pls_deletar_devolucao_notific ( nr_sequencia_p bigint) AS $body$
DECLARE


nr_seq_notificacao_w		pls_notificacao_devolucao.nr_seq_notificacao%type;
qt_devolucao_w			varchar(2);


BEGIN

select 	nr_seq_notificacao
into STRICT	nr_seq_notificacao_w
from	pls_notificacao_devolucao
where	nr_sequencia = nr_sequencia_p;

delete from 	pls_notificacao_devolucao
where	nr_sequencia = nr_sequencia_p;

select	count(1)
into STRICT	qt_devolucao_w
from	pls_notificacao_devolucao
where	nr_seq_notificacao = nr_seq_notificacao_w;

if (qt_devolucao_w = 0) then
	update  pls_notificacao_pagador
	set	ie_status		= ie_status_ant,
		ie_status_ant		 = NULL,
		dt_atualizacao		= clock_timestamp(),
		ie_tipo_notificacao	 = NULL
	where 	nr_sequencia 		= nr_seq_notificacao_w
	and	(ie_status_ant IS NOT NULL AND ie_status_ant::text <> '');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
