CREATE OR REPLACE PROCEDURE pls_deletar_prest_local_atend ( nr_seq_prestador_p bigint, nr_seq_local_atend_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_fim_vigencia_w		timestamp;

nr_seq_usuario_web_w		bigint;

qt_registros_w			bigint;
qt_reg_prestador_w		bigint	:= 0;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_usuario_web
	where	nr_seq_local_atend	= nr_seq_local_atend_p;


BEGIN

if (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') and (nr_seq_local_atend_p IS NOT NULL AND nr_seq_local_atend_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		nr_seq_usuario_web_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	count(*)
		into STRICT	qt_registros_w
		from	pls_prestador_usuario_web
		where	nr_seq_usuario		= nr_seq_usuario_web_w
		and	nr_seq_prestador	= nr_seq_prestador_p;

		if (qt_registros_w	> 0) then
			qt_reg_prestador_w	:= qt_reg_prestador_w + qt_registros_w;
		end if;
		end;
	end loop;
	close C01;

	if	((qt_reg_prestador_w - 1)	= 0) then
		begin
		select	dt_fim_vigencia
		into STRICT	dt_fim_vigencia_w
		from	local_atend_med_prest
		where	nr_seq_prestador	= nr_seq_prestador_p
		and	nr_seq_local_atend	= nr_seq_local_atend_p;
		exception
		when others then
			dt_fim_vigencia_w	:= null;
		end;

		if (coalesce(dt_fim_vigencia_w::text, '') = '') then
			update	local_atend_med_prest
			set	dt_fim_vigencia		= clock_timestamp()
			where	nr_seq_prestador	= nr_seq_prestador_p
			and	nr_seq_local_atend	= nr_seq_local_atend_p;
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
