SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_deleta_participante ( nr_seq_proc_partic_p pls_proc_participante.nr_sequencia%type) AS $body$
DECLARE


qt_intercambio_w	integer;
nr_seq_conta_proc_w	pls_conta_proc.nr_sequencia%type;


BEGIN

select	sum(qt_inter)
into STRICT	qt_intercambio_w
from (
	SELECT	count(1) qt_inter
	from	pls_int_cta_part
	where	nr_seq_proc_partic = nr_seq_proc_partic_p
	
union all

	SELECT	count(1) qt_inter
	from	ptu_nota_cobranca_reemb
	where	nr_seq_proc_partic = nr_seq_proc_partic_p
	
union all

	select	count(1) qt_inter
	from	ptu_nota_servico_item
	where	nr_seq_proc_partic = nr_seq_proc_partic_p
	
union all

	select	count(1) qt_inter
	from	ptu_nota_servico_rrs
	where	nr_seq_proc_partic = nr_seq_proc_partic_p) alias5;

if (qt_intercambio_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(857652);
end if;

delete	FROM pls_hist_analise_conta
where	nr_seq_proc_partic = nr_seq_proc_partic_p;

delete	FROM pls_analise_conta_item
where	nr_seq_proc_partic = nr_seq_proc_partic_p;

delete	FROM pls_analise_conta_parecer
where	nr_seq_proc_partic = nr_seq_proc_partic_p;

delete	FROM pls_analise_fluxo_item
where	nr_seq_proc_partic = nr_seq_proc_partic_p;

delete	FROM pls_conta_glosa
where	nr_seq_proc_partic = nr_seq_proc_partic_p;

delete	FROM pls_ocorrencia_benef
where	nr_seq_proc_partic = nr_seq_proc_partic_p;

select	max(nr_seq_conta_proc)
into STRICT	nr_seq_conta_proc_w
from	pls_proc_participante
where	nr_sequencia = nr_seq_proc_partic_p;

delete	FROM pls_conta_medica_resumo
where	nr_seq_conta_proc = nr_seq_conta_proc_w
and	nr_seq_conta_proc_partic = nr_seq_proc_partic_p;

delete 	FROM pls_proc_participante
where	nr_sequencia = nr_seq_proc_partic_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
