SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_delete_log_cp_cta_tempo () AS $body$
DECLARE

 
tb_sequencia_w	pls_util_cta_pck.t_number_table;

C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_cp_cta_log_tempo 
	where	dt_inicio_processo <= trunc(clock_timestamp() - interval '3 days') + ((1/24) * 4);


BEGIN 
-- limpa a table 
tb_sequencia_w.delete;
 
open C01;
loop 
	fetch C01 bulk collect into tb_sequencia_w 
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_sequencia_w.count = 0;
 
	-- deleta tudo que esteja 'fora' do prazo de tempo 
	forall i in tb_sequencia_w.first..tb_sequencia_w.last 
		delete	from pls_cp_cta_log_tempo 
		where	nr_sequencia = tb_sequencia_w(i);
	commit;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_delete_log_cp_cta_tempo () FROM PUBLIC;

