SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_delete_log_processo () AS $body$
DECLARE

 
tb_sequencia_w	pls_util_cta_pck.t_number_table;

-- busca todos os registros que tem mais de 30 dias que foram gravados levando em consideração o horário de uma hora da madrugada 
C01 CURSOR FOR 
	SELECT	nr_sequencia nr_seq_processo 
	from	pls_cta_log_exec 
	where	dt_inicio <= trunc(clock_timestamp() - interval '30 days') + ((1/24) * 1);

BEGIN
 
open C01;
loop 
	-- Limpa os registros da lista para que não seja processado o mesmo registro mais de uma vez. 
	tb_sequencia_w.delete;
	 
	-- Executa o fetch de um número determinado de linhas do cursor na lista. 
	fetch C01 bulk collect into tb_sequencia_w limit pls_util_pck.qt_registro_transacao_w;
 
	-- Quando o cursor não tiver mais linhas então sai do loop. 
	exit when tb_sequencia_w.count = 0;
	 
	-- apaga os registros de erros do processo 
	forall i in tb_sequencia_w.first..tb_sequencia_w.last 
		delete from pls_cta_log_erro 
		where	nr_seq_log_exec = tb_sequencia_w(i);
	commit;
	 
	-- apaga os registros do detalhe do processo 
	forall i in tb_sequencia_w.first..tb_sequencia_w.last 
		delete from pls_cta_log_exec_desc 
		where	nr_seq_log_exec = tb_sequencia_w(i);
	commit;
				 
	-- apaga os registros do processo 
	forall i in tb_sequencia_w.first..tb_sequencia_w.last 
		delete from pls_cta_log_exec 
		where	nr_sequencia = tb_sequencia_w(i);
	commit;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_delete_log_processo () FROM PUBLIC;

