CREATE OR REPLACE PROCEDURE pls_del_cad_usuario_web (cd_usuario_plano_p text, ie_situacao_benef_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ie_commit_p text) AS $body$
DECLARE


nr_seq_segurado_w	pls_segurado.nr_sequencia%type;
nr_seq_usuario_ops_w	pls_segurado_web.nr_sequencia%type;
nm_usuario_ops_w		wsuite_termo_uso_leitura.ds_login%type;

C01 CURSOR FOR
SELECT 	nr_sequencia
from	pls_segurado_web
where	nr_seq_segurado = nr_seq_segurado_w
and		((coalesce(ie_situacao_benef_p::text, '') = '') or ( (ie_situacao_benef_p IS NOT NULL AND ie_situacao_benef_p::text <> '') and ie_situacao = ie_situacao_benef_p));
				
BEGIN

nr_seq_segurado_w :=  pls_obter_dados_carteira(null,cd_usuario_plano_p,'S');
if (coalesce(nm_usuario_p::text, '') = '') then
	nm_usuario_ops_w := substr(tws_get_name_person(pls_obter_dados_segurado(nr_seq_segurado_w,'PF'),cd_estabelecimento_p),1,10);
else
	nm_usuario_ops_w := nm_usuario_p;
end if;

for r_C01_w in C01 loop
	
	nr_seq_usuario_ops_w := r_C01_w.nr_sequencia;
	
	delete from wsuite_termo_uso_leitura
	where	nr_seq_usuario_ops = nr_seq_usuario_ops_w
	and		ds_login = nm_usuario_ops_w;


end loop;

delete	from pls_segurado_web
where	nr_seq_segurado = nr_seq_segurado_w
and		((coalesce(ie_situacao_benef_p::text, '') = '') or ( (ie_situacao_benef_p IS NOT NULL AND ie_situacao_benef_p::text <> '') and ie_situacao = ie_situacao_benef_p));

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
