SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desapropriar_proximo_pag ( nr_seq_vencimento_p bigint, nm_usuario_p text, ie_commit_p text default 'S') AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Desapropriar o vencimento do próximo pagamento, para gerar título a receber para o vencimento.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_lote_pgto_w	pls_lote_pagamento.nr_sequencia%type;


BEGIN
if (nr_seq_vencimento_p IS NOT NULL AND nr_seq_vencimento_p::text <> '') then
	/* Verifica  se já foi gerado movimento e lote de pagamento para a apropriação do valor negativo */

	select 	max(a.nr_seq_lote_pgto)
	into STRICT	nr_seq_lote_pgto_w
	from	pls_evento_movimento 		a,
		pls_pag_prest_vencimento	b
	where	a.nr_sequencia = b.nr_seq_evento_movto
	and	b.nr_sequencia	= nr_seq_vencimento_p;

	if (nr_seq_lote_pgto_w IS NOT NULL AND nr_seq_lote_pgto_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(288505, 'NR_SEQ_LOTE=' || nr_seq_lote_pgto_w);
	end if;

	update	pls_pag_prest_vencimento
	set	ie_proximo_pgto = 'N',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia 	= nr_seq_vencimento_p;

	update	pls_pag_prest_venc_valor
	set	ie_tipo_valor		= 'TR',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_vencimento 	= nr_seq_vencimento_p;

	if (coalesce(ie_commit_p, 'S') = 'S') then
		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
