SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desaprovar_proposta ( nr_seq_proposta_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_proposta_beneficiario 
	where	nr_seq_proposta	= nr_seq_proposta_p;

nr_seq_contrato_w		bigint;
nr_seq_beneficiario_w		bigint;
ie_status_w			pls_proposta_adesao.ie_status%type;
qt_analise_adesao_w		integer;
qt_analise_adesao_pendente_w	integer;


BEGIN 
 
--Obter se existe um contrato gerado para essa proposta de adesão 
nr_seq_contrato_w	:= substr(pls_obter_dados_proposta(nr_seq_proposta_p,'C'),1,10);
if (coalesce(nr_seq_contrato_w,0)	> 0) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(262206, 'NR_SEQ_CONTRATO=' || nr_seq_contrato_w);
	--Mensagem: Não é permitido desaprovar uma proposta que possui contrato gerado! (Contrato NR_SEQ_CONTRATO) 
end if;
 
select	count(1) 
into STRICT	qt_analise_adesao_w 
from	pls_analise_adesao 
where	nr_seq_proposta	= nr_seq_proposta_p;
 
if (qt_analise_adesao_w > 0) then 
	select	count(1) 
	into STRICT	qt_analise_adesao_pendente_w 
	from	pls_analise_adesao 
	where	nr_seq_proposta	= nr_seq_proposta_p 
	and	coalesce(dt_encerramento::text, '') = '';
	 
	if (qt_analise_adesao_pendente_w > 0) then 
		ie_status_w	:= 'N';
	else 
		ie_status_w	:= 'M';
	end if;
else 
	ie_status_w	:= 'E';
end if;
 
update	pls_proposta_adesao 
set	ie_status	= ie_status_w, 
	nm_usuario	= nm_usuario_p, 
	dt_atualizacao	= clock_timestamp() 
where	nr_sequencia	= nr_seq_proposta_p;
 
--aaschlote 02/05/2011 OS - 314422 - Gerar historico para o beneficiário 
open C01;
loop 
fetch C01 into 
	nr_seq_beneficiario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL pls_gravar_histor_prop_benef(nr_seq_beneficiario_w,clock_timestamp(),'7','Desfeita a aprovação',nm_usuario_p);
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_desaprovar_proposta ( nr_seq_proposta_p bigint, nm_usuario_p text) FROM PUBLIC;

