CREATE OR REPLACE PROCEDURE pls_desbloquear_auto_cartao ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, ie_acao_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


/*
ie_acao_p

A               Aprovacao do contrato
L               Liberacao apos a aprovacao
R               Renovacao da carteirinha
V               Via adicional
M               Migracao de contrato
S               Aprovacao subestipulante
*/
					
qt_regra_desbloqueio_w		integer;					
qt_idade_w			integer;
qt_regra_w			integer;
ie_titularidade_w		varchar(1);
nr_seq_prop_benef_online_w	pls_proposta_benef_online.nr_sequencia%type;
nr_contrato_w			pls_contrato.nr_contrato%type;
nr_seq_plano_w			pls_plano.nr_sequencia%type;
ie_erro_w			varchar(1);


BEGIN

ie_erro_w	:= 'N';

begin
	select	obter_idade(c.dt_nascimento, b.dt_inicio_vigencia, 'A'),
		CASE WHEN coalesce(a.nr_seq_titular::text, '') = '' THEN  'T'  ELSE 'D' END ,
		a.nr_seq_prop_benef_online,
		d.nr_contrato,
		a.nr_seq_plano
	into STRICT	qt_idade_w,
		ie_titularidade_w,
		nr_seq_prop_benef_online_w,
		nr_contrato_w,
		nr_seq_plano_w
	from	pls_segurado a,
		pls_segurado_carteira b,
		pessoa_fisica c,
		pls_contrato d
	where	c.cd_pessoa_fisica = a.cd_pessoa_fisica
	and	a.nr_sequencia = b.nr_seq_segurado
	and	d.nr_sequencia = a.nr_seq_contrato
	and	a.nr_sequencia = nr_seq_segurado_p;
exception
when others then
	ie_erro_w	:= 'S';
end;

if (ie_erro_w = 'N') then
	select	count(1)
	into STRICT	qt_regra_desbloqueio_w
	from	pls_regra_desbloqueio_cart
	where	cd_estabelecimento = cd_estabelecimento_p
	and	ie_situacao = 'A'
	and	((coalesce(ie_tipo_renovacao::text, '') = '') or (ie_tipo_renovacao = ie_acao_p))
	and	((ie_titularidade = 'A') or (ie_titularidade = ie_titularidade_w))
	and	((ie_proposta_online = 'N') or (ie_proposta_online = 'S' AND nr_seq_prop_benef_online_w IS NOT NULL AND nr_seq_prop_benef_online_w::text <> ''))
	and	((coalesce(nr_seq_plano::text, '') = '') or (nr_seq_plano = nr_seq_plano_w))
	and	((coalesce(nr_contrato::text, '') = '') or (nr_contrato = nr_contrato_w))
	and	qt_idade_w between coalesce(qt_idade_inicial,0) and coalesce(qt_idade_final,9999999999);

	if (qt_regra_desbloqueio_w > 0) then
		update	pls_segurado_carteira
		set	nm_usuario_desbloqueio 	= nm_usuario_p,
			dt_desbloqueio		= clock_timestamp(),
			ie_tipo_desbloqueio	= 'A',
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_seq_segurado 	= nr_seq_segurado_p;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
