CREATE OR REPLACE PROCEDURE pls_desbloquear_cartao ( nr_seq_segurado_carteira_p pls_segurado_carteira.nr_sequencia%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, ie_desbloquear_depend_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	nr_seq_segurado_carteira_p nr_sequencia,
		nr_seq_segurado_p nr_seq_segurado
	
	
union all

	SELECT	b.nr_sequencia,
		a.nr_sequencia nr_seq_segurado
	from	pls_segurado a,
		pls_segurado_carteira b
	where	a.nr_sequencia  = b.nr_seq_segurado
	and	coalesce(b.dt_desbloqueio::text, '') = ''
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	coalesce(a.dt_cancelamento::text, '') = ''
	and	ie_desbloquear_depend_p = 'S'
	and	a.nr_seq_titular = nr_seq_segurado_p;

BEGIN

for c01_w in C01 loop
	begin
	update	pls_segurado_carteira
	set	dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_desbloqueio	= clock_timestamp(),
		nm_usuario_desbloqueio = nm_usuario_p,
		ie_tipo_desbloqueio = 'M'
	where	nr_sequencia	= c01_w.nr_sequencia;
	
	CALL pls_gerar_segurado_historico(	c01_w.nr_seq_segurado, '111', clock_timestamp(), wheb_mensagem_pck.get_texto(1126188),
					wheb_mensagem_pck.get_texto(1126189), null, null,
					null, null, clock_timestamp(),
					null, null, null,
					null, null, null,
					nm_usuario_p, 'N');
	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
