CREATE OR REPLACE PROCEDURE pls_desconsiderar_limite_dep ( nr_seq_segurado_p bigint, ds_motivo_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_contrato_w		bigint;
ds_historico_w		varchar(255);
nr_seq_seg_historico_w	bigint;


BEGIN

select	nr_seq_contrato
into STRICT	nr_seq_contrato_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

ds_historico_w	:= 'Desconsiderado o beneficiário do limite de dependência';

update	pls_segurado
set	ie_controla_dt_dependencia	= 'N'
where	nr_sequencia	= nr_seq_segurado_p;

select	nextval('pls_segurado_historico_seq')
into STRICT	nr_seq_seg_historico_w
;

CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '40', clock_timestamp(), ds_historico_w,
				ds_motivo_p, null, null, null,
				null, clock_timestamp(), null, null,
				null, null, null, null,
				nm_usuario_p, 'N');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
