CREATE OR REPLACE PROCEDURE pls_desfazer_acao_analise_rec ( nr_seq_analise_rec_p pls_analise_conta_rec.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_proc_rec_w		pls_analise_conta_rec.nr_seq_proc_rec%type;
nr_seq_mat_rec_w		pls_analise_conta_rec.nr_seq_mat_rec%type;
nr_seq_conta_rec_w		pls_analise_conta_rec.nr_seq_conta_rec%type;


BEGIN
select	nr_seq_proc_rec,
	nr_seq_mat_rec,
	nr_seq_conta_rec
into STRICT	nr_seq_proc_rec_w,
	nr_seq_mat_rec_w,
	nr_seq_conta_rec_w
from	pls_analise_conta_rec
where	nr_sequencia		= nr_seq_analise_rec_p;

update	pls_analise_conta_rec
set	vl_acatado		= 0,
	ie_situacao		= 'N'
where	nr_sequencia		= nr_seq_analise_rec_p;

if (nr_seq_proc_rec_w IS NOT NULL AND nr_seq_proc_rec_w::text <> '') then
	update	pls_rec_glosa_proc
	set	vl_acatado		= 0,
		ie_status		= '4',
		ie_acao_total		= 'N'
	where	nr_sequencia		= nr_seq_proc_rec_w;

	delete	FROM pls_analise_parecer_rec
	where	nr_seq_proc_rec = nr_seq_proc_rec_w;

elsif (nr_seq_mat_rec_w IS NOT NULL AND nr_seq_mat_rec_w::text <> '') then
	update	pls_rec_glosa_mat
	set	vl_acatado		= 0,
		ie_status		= '4',
		ie_acao_total		= 'N'
	where	nr_sequencia		= nr_seq_mat_rec_w;

	delete	FROM pls_analise_parecer_rec
	where	nr_seq_mat_rec = nr_seq_mat_rec_w;
end if;

update	pls_rec_glosa_conta
set	ie_acao_total = 'N'
where	nr_sequencia = nr_seq_conta_rec_w;

-- Atualizar valor recurso de glosa
CALL pls_atualizar_valor_recurso( nr_seq_conta_rec_w, 'C', nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
