CREATE OR REPLACE PROCEDURE pls_desfazer_aguardo_anexo ( nr_seq_guia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_observacao_w		varchar(255);


BEGIN

update	pls_auditoria
set	ie_status = 'A'
where	nr_seq_guia = nr_seq_guia_p;

ds_observacao_w	:= 'O usuário ' || nm_usuario_p || ' alterou o status da análise Aguardando Anexo Guia TISS para Análise';
CALL pls_guia_gravar_historico( nr_seq_guia_p, 2, ds_observacao_w, '', nm_usuario_p);

commit;

end;
