CREATE OR REPLACE PROCEDURE pls_desfazer_analise_disc_cont (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_item_w		bigint;


BEGIN
select	max(nr_seq_item)
into STRICT	nr_seq_item_w
from	w_pls_discussao_item
where	nr_sequencia = nr_sequencia_p;

update	w_pls_discussao_item
set	qt_aceita	= 0,
	vl_aceito	= 0,
	qt_negada	= 0,
	vl_negado	= 0,
	ie_situacao	 = NULL
where	nr_sequencia 	= nr_sequencia_p;

update	w_pls_discussao_item
set	ie_situacao 	 = NULL
where	nr_seq_item	= nr_seq_item_w
and	(nr_seq_partic_proc IS NOT NULL AND nr_seq_partic_proc::text <> '');

delete
from	pls_analise_parecer_disc
where	nr_seq_w_disc_item = nr_sequencia_p;

delete
from	pls_hist_analise_conta
where	nr_seq_item = nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
