SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_aprovacao_repasse ( nr_seq_repasse_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_titulos_w	integer;


BEGIN

select	count(*)
into STRICT	qt_titulos_w
from	pls_repasse_vend_venc a,
		titulo_pagar b
where	a.nr_titulo	= b.nr_titulo
and		a.nr_seq_repasse = nr_seq_repasse_p;

if (qt_titulos_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265794,'');
	--Operação cancelada. Existem títulos para este repasse !
end if;

update	pls_repasse_vend
set	dt_aprovacao		 = NULL,
	nm_usuario_aprov	 = NULL,
	nm_usuario			= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia	= nr_seq_repasse_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
