SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_auditoria_item ( nr_seq_item_p bigint, ie_tipo_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/* IE_TIPO_ITEM_P 
	1 - Procedimento 
	2 - Material 
*/
 
 
ie_status_w			varchar(2);
nr_seq_guia_w			bigint;


BEGIN 
if (ie_tipo_item_p	= 1) then 
	select	coalesce(ie_status,'X'), 
		nr_seq_guia 
	into STRICT	ie_status_w, 
		nr_seq_guia_w 
	from	pls_guia_plano_proc 
	where	nr_sequencia	= nr_seq_item_p;
	 
	if (ie_status_w in ('L','P','S','M')) then 
		update	pls_guia_plano_proc 
		set	ie_status		= 'A', 
			dt_atualizacao		= clock_timestamp(), 
			nm_usuario		= nm_usuario_p, 
			qt_autorizada		= qt_solicitada, 
			dt_liberacao		 = NULL, 
			nm_usuario_liberacao	 = NULL 
		where	nr_sequencia		= nr_seq_item_p;
		 
		delete	from pls_guia_motivo_lib 
		where	nr_seq_guia_proc	= nr_seq_item_p;
		 
        if (ie_status_w = 'M') then 
			delete	from pls_guia_glosa 
			where	nr_seq_guia_proc = nr_seq_item_p;
		end if;		
	end if;
elsif (ie_tipo_item_p	= 2) then 
	select	coalesce(ie_status,'X'), 
		nr_seq_guia 
	into STRICT	ie_status_w, 
		nr_seq_guia_w 
	from	pls_guia_plano_mat 
	where	nr_sequencia	= nr_seq_item_p;
	 
	if (ie_status_w in ('L','P','S','M')) then 
		update	pls_guia_plano_mat 
		set	ie_status		= 'A', 
			dt_atualizacao		= clock_timestamp(), 
			nm_usuario		= nm_usuario_p, 
			qt_autorizada		= qt_solicitada, 
			dt_liberacao		 = NULL, 
			nm_usuario_liberacao	 = NULL 
		where	nr_sequencia		= nr_seq_item_p;
		 
		delete	from pls_guia_motivo_lib 
		where	nr_seq_guia_mat	= nr_seq_item_p;
		 
        if (ie_status_w = 'M') then 
			delete	from pls_guia_glosa 
			where	nr_seq_guia_mat	= nr_seq_item_p;
		end if;		
	end if;
end if;
 
CALL pls_atualizar_estagio_guia(nr_seq_guia_w, nm_usuario_p,null);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
