CREATE OR REPLACE PROCEDURE pls_desfazer_auditoria_req ( nr_seq_requisicao_p bigint ) AS $body$
DECLARE


nr_seq_auditoria_w	bigint;


BEGIN

begin
	select	nr_sequencia
	into STRICT	nr_seq_auditoria_w
	from	pls_auditoria
	where	nr_seq_requisicao	= nr_seq_requisicao_p;
exception
when others then
	nr_seq_auditoria_w	:= null;
end;

if (nr_seq_auditoria_w IS NOT NULL AND nr_seq_auditoria_w::text <> '') then
	update	pls_alerta_evento_controle
	set	nr_seq_auditoria  = NULL
	where	nr_seq_auditoria = nr_seq_auditoria_w;

	delete	from pls_analise_ocor_glosa_aut
	where	nr_seq_auditoria = nr_seq_auditoria_w;

	delete	from pls_ocorrencia_benef
	where	nr_seq_requisicao = nr_seq_requisicao_p;

	delete	from pls_auditoria_anexo
	where	nr_seq_auditoria = nr_seq_auditoria_w;

	delete	from pls_auditoria_grupo
	where	nr_seq_auditoria = nr_seq_auditoria_w;

	delete	from pls_auditoria_item
	where	nr_seq_auditoria = nr_seq_auditoria_w;

	delete	from pls_auditoria
	where	nr_seq_requisicao	= nr_seq_requisicao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
