SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_auditoria ( nr_seq_guia_p bigint) AS $body$
DECLARE


nr_seq_auditoria_w		bigint;
qt_reg_w			bigint;
nr_seq_analise_w		bigint;
cd_guia_w			varchar(20);

C01 CURSOR FOR
	SELECT	nr_sequencia
	into STRICT	nr_seq_auditoria_w
	from	pls_auditoria
	where	nr_seq_guia	= nr_seq_guia_p;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	pls_guia_plano_historico
where	nr_seq_guia = nr_seq_guia_p
and	(nr_seq_item IS NOT NULL AND nr_seq_item::text <> '');

if (qt_reg_w = 0) then
	delete	from pls_guia_plano_historico
	where	nr_seq_guia	= nr_seq_guia_p
	and	(nr_seq_item IS NOT NULL AND nr_seq_item::text <> '');

	open C01;
	loop
	fetch C01 into
		nr_seq_auditoria_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		update	pls_alerta_evento_controle
		set	nr_seq_auditoria  = NULL
		where	nr_seq_auditoria = nr_seq_auditoria_w;

		delete	from pls_analise_ocor_glosa_aut
		where	nr_seq_auditoria = nr_seq_auditoria_w;

		delete	from pls_auditoria_anexo
		where	nr_seq_auditoria = nr_seq_auditoria_w;

		delete	from pls_auditoria_grupo
		where	nr_seq_auditoria = nr_seq_auditoria_w;

		delete	from pls_auditoria_item
		where	nr_seq_auditoria = nr_seq_auditoria_w;
		end;
	end loop;
	close C01;

	delete	from pls_auditoria
	where	nr_seq_guia	= nr_seq_guia_p;
else
	select	max(nr_sequencia)
	into STRICT	nr_seq_analise_w
	from	pls_auditoria
	where	nr_seq_guia = nr_seq_guia_p;

	select	cd_guia
	into STRICT	cd_guia_w
	from	pls_guia_plano
	where	nr_sequencia = nr_seq_guia_p;

	CALL wheb_mensagem_pck.exibir_mensagem_abort(188970,'CD_GUIA=' || cd_guia_w || ';' || 'NR_SEQ_ANALISE='||nr_seq_analise_w);
	/*'Processo de auditoria da guia '||cd_guia_w||  já iniciado na função OPS - Análise de autorizações, análise '||nr_seq_analise_w||'.'||'#@#@');*/

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_desfazer_auditoria ( nr_seq_guia_p bigint) FROM PUBLIC;

