CREATE OR REPLACE PROCEDURE pls_desfazer_calculo_reajuste ( nr_seq_reajuste_p bigint, nm_usuario_p text) AS $body$
DECLARE



qt_registros_w		bigint;
qt_registros_ww		bigint;
nr_seq_tabela_w		bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_reajuste_tabela
	where	nr_seq_reajuste	= nr_seq_reajuste_p
	and	coalesce(dt_liberacao::text, '') = '';


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	pls_reajuste_tabela
where	nr_seq_reajuste	= nr_seq_reajuste_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

if (qt_registros_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 238969, null );
	/* Os reajustes das tabelas já foram liberados. O cálculo não pode ser desfeito! */

end if;

open c01;
loop
fetch c01 into
	nr_seq_tabela_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	delete	from pls_reajuste_preco
	where	nr_seq_tabela	= nr_seq_tabela_w;

	update	pls_reajuste_tabela
	set	dt_liberacao	 = NULL
	where	nr_sequencia	= nr_seq_tabela_w;
end loop;
close c01;

update	pls_reajuste
set	ie_status	= '1',
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_reajuste_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
