CREATE OR REPLACE PROCEDURE pls_desfazer_calculo_tps ( nr_seq_tps_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (nr_seq_tps_p IS NOT NULL AND nr_seq_tps_p::text <> '') then
	delete
	from 	pls_tps_reg
	where	nr_seq_tps	= nr_seq_tps_p;

	update	pls_tps
	set	vl_base			 = NULL,
		vl_desconto_mp		 = NULL,
		vl_desc_cota_unica	 = NULL,
		vl_liquido		 = NULL,
		vl_calculo		 = NULL,
		nr_seq_regra_desconto	 = NULL,
		pr_desconto		 = NULL,
		dt_geracao_lote		 = NULL,
		dt_sugerida_pagamento	 = NULL
	where	nr_sequencia		= nr_seq_tps_p;

	commit;
end if;
end;
$body$
